import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-diretivas-atributos',
  templateUrl: './diretivas-atributos.component.html',
  styleUrls: ['./diretivas-atributos.component.scss']
})
export class DiretivasAtributosComponent implements OnInit{

  public valor: boolean = true;
  public height: string = "20px";
  public colorPx: string = "red"
  public nome: string = ""

  constructor(){}
  ngOnInit(): void {

      setInterval(()=>{
        if(this.valor){
          this.valor = false
        }
        else{
          this.valor = true
        }

        if(this.height == "20px"){
          this.height = "50px";
          this.colorPx = "green"
        }
        else{
          this.height = "20px"
          this.colorPx = "purple"

        }
      }, 2000)
  }
}
<p [ngClass]="{'active': valor, 'disable': !valor}"> NgClass </p>
<p [ngStyle]="{'height': height, 'background': colorPx}"> NgClass </p>

<hr>

<input [(ngModel)] = "nome"> {{nome}}
