import { Component, OnInit } from '@angular/core';

@Component({
    selector: 'app-diretivas-estruturais',
    templateUrl: './diretivas-estruturais.component.html',
    styleUrls: ['./diretivas-estruturais.component.scss']
})
export class DiretivasEstruturaisComponent implements OnInit {

    public condition: boolean = true
    public conditionClick: boolean = true
    public list:Array<{nome:string, idade:number}> = [
        {nome: 'João', idade:25}, 
        {nome: 'Tallita', idade:24},
        {nome: 'Pedro', idade: 20}
    ]


    constructor() { }
    ngOnInit(): void {

        setInterval(()=>{
            if(this.condition){
                this.condition = false
            }else{
                this.condition = true
            }
        }, 2000)

    }

    public onnClick(){
        if(this.conditionClick){
            this.conditionClick = false
        }else{
            this.conditionClick = true
        }
    }

    public onnClickAddList(){
        this.list.push({nome: "Nay", idade: 31})
    }

    public onClickEventList(event: number){
        this.list.splice(event, 1)
    }
}
<h1>NGIF</h1>

<p *ngIf="condition; else elseBlock">True</p>
<ng-template #elseBlock>
    <p>False</p>
</ng-template>

<hr>

<p *ngIf="conditionClick">
    True
</p>

<p *ngIf="!conditionClick">
    False
</p>

<button (click)="onnClick()">Clicar</button>
<br>
<br>
<hr>

<h1>NGFOR</h1>

<ul>
    <li *ngFor="let item of list; let i = index;" (click)="onClickEventList(i)">[{{i}}] {{item.nome}} - idade = {{item.idade}}</li>
</ul>

<button (click)="onnClickAddList()">Add</button>

<br>
<br>
<hr>

<h1>NGSWITCH</h1>

<div [ngSwitch]="'joao'">
    <p *ngSwitchCase="'joao'">
        João
    </p>
    <p *ngSwitchCase="'tallita'">
        João
    </p>
</div>
